            <NavDropdown.Item href="#action/3.1">Edible</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">Poisonous</NavDropdown.Item>
          </NavDropdown>
        </Container>
      </Navbar>

      <RecordList /> {/*open recordList with filter option in props*/}

      {/* </Route> */}
      {/* <Route path="/edit/:id" component={Edit} />
      <Route path="/create">
